mapper.select:
select phone_id, sku, image_url, detail_url, title, price, website_id
from tb_phone
where price > 800

    and website_id = #{websiteId}


    and match(title) against(#{title});

select avg(price)
from tb_phone
where price > 800

    and website_id = #{websiteId}


    and match(title) against(#{title});

select max(price)
from tb_phone
where price > 800

    and website_id = #{websiteId}


    and match(title) against(#{title});

select min(price)
from tb_phone
where price > 800

    and website_id = #{websiteId}


    and match(title) against(#{title});
